#!/bin/bash

GAWK=gawk
SED=sed

$GAWK '/<wordlist/,/<\/wordlist/ {print;}' < joukahainen.xml |\
    fgrep -v wordlist |\
    tr -d '\n' |\
    # XML to TSV
    $SED -e 's/<word /\n/g' |\
    fgrep -v prefix |\
    $SED -e 's/>[[:space:]]*</></g' |\
    $SED -e 's/^id=.*<forms>//' \
        -e 's:<form>::' \
        -e 's:</form><form>:||:g' \
        -e 's:</form>::' |\
    $SED -e 's:</forms>.*<classes>:\t:' \
        -e 's:<wclass>::' \
        -e 's:</wclass><wclass>:||:g' \
        -e 's:</wclass>::' |\
    $SED -e 's:</classes>.*<inflection>:_:' \
        -e 's:<infclass>::' \
        -e 's:</infclass><infclass[^>]*>:||:g' \
        -e 's:</infclass>::' |\
    $SED -e 's:</classes></word>::' \
        -e 's:</classes>::' |\
    $SED -e 's:<vtype>:__:' \
        -e 's:</vtype>::' \
        -e 's:</style>::' \
        -e 's:<usage>::' \
        -e 's:</usage>::' \
        -e 's:</derivation>::' \
        -e 's:</compounding>::' \
        -e 's:</grammar>::' \
        -e 's:<info>.*</info>::' \
        -e 's:<frequency>.*</frequency>::' \
        -e 's:<application>::' \
        -e 's:</application>::' \
        -e 's:<flag>.*</flag>::' |\
    $SED -e 's:</inflection>.*</word>::'\
        -e 's:<style>.*</word>::' \
        -e 's:</word>::' |\
    # JOU to new paras
    $SED -e 's/pnoun_\(misc\|lastname\|firstname\|place\)/PROPN/g' \
        -e 's/noun/NOUN/g' \
        -e 's/verb\([^i]\)/VERB\1/g' \
